    import java.io.*;    import java.math.BigInteger;    import java.util.*;         public class Codeforces {        public static void main(String args[]) throws FileNotFoundException, IOException {            Scanner scan = new Scanner(System.in);                int t=scan.nextInt();                while(t-->0){                    int arr[]=new int[3];                    arr[0]=scan.nextInt();                    arr[1]=scan.nextInt();                    arr[2]=scan.nextInt();                    Arrays.sort(arr);                    int count=0;                    if(arr[0]>0 || arr[1]>0 || arr[2]>0){                        if(arr[0]>0){                            arr[0]--;                            count++;                        }                        if(arr[1]>0){                            arr[1]--;                            count++;                        }                        if(arr[2]>0){                            arr[2]--;                            count++;                        }                    }                    if(arr[2]>0 && arr[1]>0){                        arr[2]--;                        arr[1]--;                        count+=1;                    }                    if(arr[2]>0 && arr[0]>0){                        arr[2]--;                        arr[0]--;                        count+=1;                    }                    if(arr[1]>0 && arr[0]>0){                        arr[1]--;                        arr[0]--;                        count+=1;                    }                    if(arr[2]>0 && arr[1]>0 && arr[0]>0){                        arr[2]--;                        arr[1]--;                        arr[0]--;                        count+=1;                    }                         System.out.println(count);                }                   }        }
