# ================================================  Q2t = int(input()) for _ in range(t):    n = int(input())    s = input()     mn = 'z'    for i in s:        if mn > i:            mn = i     d = []    for i in range(n):        if s[i] == mn:            if ((i+1)%2 == 0 and len(s)%2 ==0) or ((i+1)%2 != 0 and len(s)%2 != 0) :                d.append((s[i:] + s[:i][::-1], i + 1))            else:                d.append((s[i:]+s[:i],i+1))      ans = list(min(d))    print(ans[0])    print(ans[1])
