#include <cstdio>#include <algorithm>#include <cstring>#include <cmath>#include <iostream>using namespace std; string s;int pre; int main() {	getline(cin, s);	pre = 0;	for(int i = 0; i < s.size(); ++i) {		int x = (int)s[i], num[17] = {0}, newx = 0;		for(int j = 7; j >= 0; --j)			if(x >= (int)pow(2, j)) {				num[j] = 1;				x -= pow(2, j);			}		for(int j = 0; j <= 7; ++j)			newx += num[j] * pow(2, 7 - j);		printf("%d\n", (256 - (newx - pre)) % 256);		pre = newx;	}}
