//package CfProblemSet.CF626; import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.StringTokenizer; public class A {     public static void main(String[] args) {        FastScanner fs = new FastScanner();        int t = fs.nextInt();        while (t-- > 0) {            int n = fs.nextInt();            int a[] = new int[n];            int even = -1;             int sum = 0;            for (int i = 0; i < n; i++) {                a[i] = fs.nextInt();                if (even == -1 && a[i] % 2 == 0) {                    even = i;                }                if (sum < 0) {                    sum += a[i];                }            }            if (even >= 0) {                System.out.println( 1 );                System.out.println( even+1 );            } else {                if (n > 1) {                    System.out.println( 2 );                    System.out.println( "1 2" );                } else                    System.out.println( -1 );            }        }    }     static class FastScanner {        BufferedReader br = new BufferedReader( new InputStreamReader( System.in ) );        StringTokenizer st = new StringTokenizer( "" );         public String next() {            while (!st.hasMoreElements())                try {                    st = new StringTokenizer( br.readLine() );                } catch (IOException e) {                    e.printStackTrace();                }            return st.nextToken();        }         public int nextInt() {            return Integer.parseInt( next() );        }         public int[] readArray(int n) {            int[] a = new int[n];            for (int i = 0; i < n; i++)                a[i] = nextInt();            return a;        }         public long nextLong() {            return Long.parseLong( next() );        }    }}
