import java.util.*;import java.io.*; public class a{    public static void main(String[] args)    {        Scanner s=new Scanner(System.in);         int t=s.nextInt();                          while(t-->0){                          int n=s.nextInt();             int[] arr=new int[n+1];             for(int i=1;i<arr.length;i++)             {                arr[i]=s.nextInt();                 }                          if(n==1&&arr[1]%2!=0)             {                 System.out.println("-1");             }else if(arr[1]%2==0){                 System.out.println("1");                  System.out.println("1");             }else if(arr[2]%2==0){                 System.out.println("1");                  System.out.println("2");             }else{                  System.out.println("2");                   System.out.println("1 2");             }                                                }    }}
