import java.util.*;import java.io.*; public class Main{	public static void main(String[] args) {				Scanner sc= new Scanner(System.in);			int n=sc.nextInt();				while(n-->0) {			int x=sc.nextInt();						int arr[]=new int[x];						for(int i=0;i<x;i++)				arr[i]=sc.nextInt();						Arrays.sort(arr);			int count=0;			for(int i=1;i<x;i++) {				if(arr[i]>arr[i-1]) {					count++;				}			}			System.out.println(count+1);		}		}	}
