import java.math.*;import java.util.*;public class PrincessAndPrince{	public static void main(String[] args) 	{		Scanner sc = new Scanner(System.in);		int runs = sc.nextInt();		while(runs-->0)		{			int size = sc.nextInt();			boolean optimal = true;			int[] arr = new int[size+1];			int out = 0;			boolean add = false;			int[] dex = new int[2];			for(int i = 1;i<size+1;i++)			{				arr[i] = 1;							}			for(int i = 0;i<size;i++)			{				int len = sc.nextInt();				boolean fill = false;				for(int j = 0;j<len;j++)				{					int num = sc.nextInt();					if(!fill)					{						if(arr[num]==1)						{							out++;							arr[num]--;							fill = true;						}					}				}				if(!fill&&add==false)				{										dex[0] = i+1;					add = true;				}			}			if(add)			{				for(int j = 1;j<size+1;j++)					{						if(arr[j]==1)						{							arr[j]=0;							out++;							optimal = false;							dex[1] = j;							break;						}					}			}			if(optimal)				System.out.println("OPTIMAL");			else			{				System.out.println("IMPROVE");				System.out.println(dex[0]+" "+dex[1]);			}		}		}} 
