import java.io.*;import java.util.*;public class levelstatistics {	public static void main(String[] args) throws IOException {		BufferedReader f = new BufferedReader(new InputStreamReader(System.in));		StringTokenizer st = new StringTokenizer(f.readLine());		int t = Integer.parseInt(st.nextToken());		while(t > 0) {			t --;			int n = Integer.parseInt(f.readLine());			int plays = 0;			int clears = 0;			boolean yes = true;			for(int i = 0; i < n; i ++) {				st = new StringTokenizer(f.readLine());				int nextPlays = Integer.parseInt(st.nextToken());				int nextClears = Integer.parseInt(st.nextToken());				if(nextPlays - plays < nextClears - clears) {					yes = false;				}				if(nextPlays < plays || nextClears < clears) {					yes = false;				}				plays = nextPlays;				clears = nextClears;			}			if(yes) System.out.println("YES");			else System.out.println("NO");		}	}}
