//package com.company;import java.io.*;import java.util.*; public class Main {     // Use greedy ONLY when locally optimum result yields globally optimum result.     public static void main(String[] args) throws IOException {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        PrintWriter pw = new PrintWriter(System.out);        int t = Integer.parseInt(br.readLine());        while(t-->0){        StringTokenizer st = new StringTokenizer(br.readLine());        int n = Integer.parseInt(st.nextToken()),x=Integer.parseInt(st.nextToken());        Integer[] a = new Integer[n];        st = new StringTokenizer(br.readLine());        for(int i=0;i<n;i++) a[i]=Integer.parseInt(st.nextToken());        Arrays.sort(a,Collections.reverseOrder());        long sum=0;        int ans=0;        for(int i=0;i<n;i++){            sum+=a[i];            if(a[i]>=x) ans++;            else if(sum/(i+1)>=x) ans++;            else break;        }        pw.println(ans);        }        pw.flush();        pw.close();    }}
