import java.util.Arrays;import java.util.Scanner; public class Main {		public static void main(String[] args)	{		Scanner s=new Scanner(System.in);				int t=s.nextInt();				for(int i=0;i<t;i++)		{			int a=s.nextInt();			int b=s.nextInt();			int c=s.nextInt();						long[] arr=new long[a];			long[] brr=new long[b];			long[] crr=new long[c];						for(int j=0;j<a;j++)			{				arr[j]=s.nextInt();			}						for(int j=0;j<b;j++)			{				brr[j]=s.nextInt();			}						for(int j=0;j<c;j++)			{				crr[j]=s.nextInt();			}						Arrays.sort(arr);			Arrays.sort(brr);			Arrays.sort(crr);						long[] ans=new long[6];						ans[0]=find(arr,brr,crr);			ans[1]=find(brr,arr,crr);			ans[2]=find(arr,crr,brr);			ans[3]=find(brr,crr,arr);			ans[4]=find(crr,arr,brr);			ans[5]=find(crr,brr,arr);						Arrays.sort(ans);						System.out.println(ans[0]);					}			}		public static long find(long[] arr,long[] brr,long[] crr)	{		long min=Long.MAX_VALUE;				for(int i=0;i<brr.length;i++)		{			long ans1=bsl(arr,brr[i]);			long ans2=bsm(crr,brr[i]);						if(ans1!=-1&&ans2!=-1)			{				long term1=ans1-ans2;				long term2=ans1-brr[i];				long term3=ans2-brr[i];								long curr=(term1*term1)+(term2*term2)+(term3*term3);								if(curr<min)					min=curr;							}		}				return min;	}		public static long bsl(long[] arr,long k)	{		long ans=-1;				int start=0;		int end=arr.length-1;				while(start<=end)		{			int mid=(start+end)/2;						if(arr[mid]<=k)			{				ans=arr[mid];				start=mid+1;			}			else			{				end=mid-1;			}					}				return ans;			}		public static long bsm(long[] arr,long k)	{		long ans=-1;				int start=0;		int end=arr.length-1;				while(start<=end)		{			int mid=(start+end)/2;						if(arr[mid]>=k)			{				ans=arr[mid];				end=mid-1;			}			else			{				start=mid+1;			}					}				return ans;	}	}
