import java.util.*;public class Main{    public static void main(String args[])throws Exception    {	Scanner sc=new Scanner(System.in);	int n=sc.nextInt(); 	int a[]=new int[n]; 	int c=0; 	double sum=0; 	int b[]=new int[n];	for(int i=0;i<n;i++)	{		a[i]=sc.nextInt();		sum=sum+a[i];	}	for(int i=0;i<n;i++)	{		if(a[i]==(sum-a[i])/(n-1))		{			b[c]=i;			c++;		}	}	System.out.println(c);	for(int i=0;i<c;i++)	{		System.out.print(b[i]+1+" ");	}    }}
