// Working program with FastReader import java.io.*;import java.util.*;Â public class ProblemA4 {     static class FastReader     {         BufferedReader br;         StringTokenizer st;         public FastReader(){             br = new BufferedReader(new InputStreamReader(System.in));         }         String next(){             while (st == null || !st.hasMoreElements()){                 try{                     st = new StringTokenizer(br.readLine());                 }                 catch (IOException e){                     e.printStackTrace();                 }             }             return st.nextToken();         }         int nextInt(){             return Integer.parseInt(next());         }         long nextLong(){             return Long.parseLong(next());         }         double nextDouble(){             return Double.parseDouble(next());         }        String nextLine(){             String str = "";             try{                 str = br.readLine();             }             catch(IOException e){                 e.printStackTrace();             }            return str;         }    }    public static void runTest(FastReader sc)throws IOException{        int n = sc.nextInt();        int arr[] = new int[n];        int pos[] = new int[n + 1];        for(int i = 0; i < n;i++){            arr[i] = sc.nextInt();            pos[arr[i]] = i;        }                boolean marked[] = new boolean[n];        int num = 1;        int index = pos[num];        marked[index] = true;        num++;        int ans = 1;        index++;        while(num <= n){            if(index >= n){                index = pos[num];                continue;            }            else if(marked[index]){                index = pos[num];                continue;            }                else if(pos[num] != index){                 ans = 0;                 break;            }            marked[index] = true;            num++;            index++;        }                if(ans == 0)            System.out.println("NO");        else            System.out.println("YES");    }    public static void main(String args[])throws IOException{        FastReader sc = new FastReader();        int t = sc.nextInt();        while(t > 0){            runTest(sc);            t--;        }    }} 
