import java.util.*;public class Main{    public static void main(String args[])    {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while(t>0)        {            int n = sc.nextInt();            if(n%4!=0)            {                System.out.println("NO");            }            else            {                System.out.println("YES");                int e=2,o=1,evensum=0,oddsum=0;                for(int i=0;i<n/2;i++)                {                  System.out.print(e+" ");                  evensum += e;                  e = e+2;                }                for(int i=0;i<(n/2)-1;i++)                {                     System.out.print(o+" ");                     oddsum += o;                  o = o+2;                }                System.out.println(evensum - oddsum);            }            t--;        }    }}
