import java.util.*;import java.io.*; public class Main{	static boolean flag[];	public static void main(String[] args) {		Scanner sc= new Scanner(System.in);						int n=sc.nextInt();				while(n-->0) {			int x=sc.nextInt();						if((x/2)%2==1) {				System.out.println("NO");			}else {				System.out.println("YES");				int eventot=0;				for(int i=2;i<=x;i+=2) {					System.out.print(i+" ");					eventot+=i;				}				int oddtot=0;				for(int i=1;i<x-2;i+=2) {					System.out.print(i+" ");					oddtot+=i;				}								System.out.println(eventot-oddtot);			}						}	}	}
