//package codechef;Â import java.util.*;import java.io.*;public class CodeChef{    public static int gcd(int a,int b)    {        if(b==0) return a;        return gcd(b,a%b);    }    public static void main(String[] args)throws IOException    {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        int t = Integer.parseInt(br.readLine());        while(t-->0)        {                String s[] = br.readLine().split(" ");            if(s[0].equals("1")||s[1].equals("1")) System.out.println("YES");            else if(s[0].equals("2")&&s[1].equals("2")) System.out.println("YES");            else System.out.println("NO");                   }    }}
