import java.util.*; public class Main{       public static void main(String[] args) {		Scanner scan = new Scanner(System.in);		int t = scan.nextInt();		while(t>0){		    int n = scan.nextInt();		    t--;		    int size = (int)Math.pow(10, 5);		    int big = (int)Math.pow(10, 9);	        long cards[] = new long[size];	        cards[0] = 0;	        cards[1] = 2;	        for(int i=2; i<size ;i++){	            cards[i] = cards[i-1] + 3*i - 1;	            if(cards[i]>big){	                break;	            }	        }	        		    int count = 0;		    while(n>=cards[1]){		        int l=0, r=25820;		        int mid = 0;		        while(l<=r){		            mid = (l+r)/2;		            if(cards[mid]>n){		                r=mid-1;		            }		            else{		                l=mid+1;		            }		        }		        n -= cards[l-1];		        count++;		    }		    System.out.println(count);		}	}}
