import java.util.Scanner;import java.lang.Math;public class NewMain{    public static void main(String args[])    {        Scanner in=new Scanner(System.in);        int t;  t=in.nextInt();        for(int j=0;j<t;j++)        {            int n;  n=in.nextInt();            int sum1=0,sum2=0;            sum1+=Math.pow(2, n);            for(int i=1;i<n/2;i++)            {                sum1+=Math.pow(2, i);            }            for(int i=n/2;i<n;i++)            {                sum2+=Math.pow(2, i);            }            System.out.println(sum1-sum2);        }    }}
