// practice with rainboyimport java.io.*;import java.util.*; public class CF135B extends PrintWriter {	CF135B() { super(System.out, true); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF135B o = new CF135B(); o.main(); o.flush();	} 	int[] xx = new int[8];	int[] yy = new int[8];	boolean rect(int i1, int i2, int i3, int i4) {		int x1 = xx[i1], y1 = yy[i1];		int x2 = xx[i2], y2 = yy[i2];		int x3 = xx[i3], y3 = yy[i3];		int x4 = xx[i4], y4 = yy[i4];		return x2 - x1 == x3 - x4 && y2 - y1 == y3 - y4			&& (x2 - x1) * (x3 - x2) + (y2 - y1) * (y3 - y2) == 0;	}	boolean square(int i1, int i2, int i3, int i4) {		int x1 = xx[i1], y1 = yy[i1];		int x2 = xx[i2], y2 = yy[i2];		int x3 = xx[i3], y3 = yy[i3];		int x4 = xx[i4], y4 = yy[i4];		return x2 - x1 == x3 - x4 && y2 - y1 == y3 - y4			&& (x2 - x1) * (x3 - x2) + (y2 - y1) * (y3 - y2) == 0			&& (x2 - x1) * (x2 - x1) + (y2 - y1) * (y2 - y1)			== (x3 - x2) * (x3 - x2) + (y3 - y2) * (y3 - y2);	}	void main() {		for (int i = 0; i < 8; i++) {			xx[i] = sc.nextInt();			yy[i] = sc.nextInt();		}		for (int a = 0; a < 8; a++)			for (int b = 0; b < 8; b++) {				if (b == a)					continue;				for (int c = 0; c < 8; c++) {					if (c == a || c == b)						continue;					for (int d = 0; d < 8; d++) {						if (d == a || d == b || d == c)							continue;						if (!square(a, b, c, d))							continue;						int e = 0;						while (e == a || e == b || e == c || e == d)							e++;						int f = e + 1;						while (f == a || f == b || f == c || f == d)							f++;						int g = f + 1;						while (g == a || g == b || g == c || g == d)							g++;						int h = g + 1;						while (h == a || h == b || h == c || h == d)							h++;						if (rect(e, f, g, h) || rect(e, f, h, g) || rect(e, g, f, h) || rect(e, g, h, f) || rect(e, h, f, g) || rect(e, h, g, f)) {							println("YES");							println((a + 1) + " " + (b + 1) + " " + (c + 1) + " " + (d + 1));							println((e + 1) + " " + (f + 1) + " " + (g + 1) + " " + (h + 1));							return;						}					}				}			}		println("NO");	}}
