/** * * @author aditya */import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.io.PrintWriter;import java.util.StringTokenizer; public class OracAndFactors {     	public static void main(String[] args) {            FastScanner fs=new FastScanner();            int T=fs.nextInt();            PrintWriter out=new PrintWriter(System.out);            for (int tt=0; tt<T; tt++) {                int n = fs.nextInt();                int k = fs.nextInt();                for(int j=1;j<=k;j++){                    if(n%2==0){                        n=n+((k-j+1)*2);                        break;                    }                    for(int i=2;i<=n;i++){                        if(n%i==0){                            n+=i;                            break;                        }                    }                }                out.println(n);            }            out.close();	}		static class FastScanner {            BufferedReader br=new BufferedReader(new InputStreamReader(System.in));            StringTokenizer st=new StringTokenizer("");            public String next() {            while (!st.hasMoreElements())		try {                    st=new StringTokenizer(br.readLine());		} catch (IOException e) {                    e.printStackTrace();		}		return st.nextToken();            }		            int[] readArray(int n) {            int[] a=new int[n];             for (int i=0; i<n; i++) a[i]=nextInt();		return a;            }            int nextInt () {                return Integer.parseInt(next());            }	} }
