import java.util.*;public class Main{	public static void main(String[] args) {		Scanner s=new Scanner(System.in);		int n=s.nextInt();		while(n-->0)		{		    int t =s.nextInt();		    ArrayList<Integer> l=new ArrayList<>();		    int res,r=1,i=0;		    while(t>0)		    {		        res=t%10;		        if(res!=0)		        {		        l.add(res*r);		        }		        t=t/10;		        r=r*10;		    }		System.out.println(l.size());		for(int j=0;j<l.size();j++)		{		System.out.print(l.get(j)+" ");    		}		}			}}
