import java.util.*;public class Main{    public static void main(String args[])    {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while(t>0)        {            int n,k;            n = sc.nextInt();            k = sc.nextInt();            int a[] = new int[n];            int b[] = new int[n];            for(int i=0;i<n;i++)            {                a[i] = sc.nextInt();            }            for(int i=0;i<n;i++)            {                b[i] = sc.nextInt();            }            Arrays.sort(a);            Arrays.sort(b);            int i=0,j=n-1,sum=0;            while(k>0 && b[j]>a[i])            {                sum += b[j--];                k--;                i++;            }            while(i<n)            {                sum += a[i++];            }            System.out.println(sum);            t--;        }    }}
