import java.util.*;import java.io.*;public class A{	public static void main(String ar[])throws Exception	{		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));		int t = Integer.parseInt(br.readLine());		while(t-- > 0)		{			String nk[] = br.readLine().split(" ");			int n = Integer.parseInt(nk[0]);			int k = Integer.parseInt(nk[1]);						int a[] = new int[n];			int b[] = new int[n];						String str1[] = br.readLine().split(" ");			for(int i =0;i<n;i++)a[i] = Integer.parseInt(str1[i]);						String str2[] = br.readLine().split(" ");			for(int i =0;i<n;i++)b[i] = Integer.parseInt(str2[i]);									System.out.println(getMaxSum(a,b,n,k));		}	}	public static int getMaxSum(int a[], int b[], int n, int k)	{		Arrays.sort(a);		Arrays.sort(b);			int count = 0;		int curr = n-1;		int sum = 0;		for(int i =0;i<n;i++)		{			if(a[i] < b[curr] && count < k)			{				sum += b[curr];				count++;				curr--;			}			else			{				sum += a[i];			}		}		return sum;	}}
