//package codeforces; import java.util.*; public class parkLighting {		public static void main(String[] args) {		Scanner s = new Scanner(System.in);				int testCases = s.nextInt();				for(int i = 0; i < testCases; i++) {			int n = s.nextInt();			int m = s.nextInt();						if(n%2 == 0 || m%2 == 0) {				System.out.println((n*m)/2);								//i.e. (4*5)/2 = 20/2 = 10			}						else {				System.out.println(   ((n*m)+1)/2          );								// i.e. ((3*5)+1)/2 = ((15)+1)/2 = 16/2 = 8			}		}					}}
