//package com.codeforces.Round645; import java.util.Arrays;import java.util.Scanner; public class Div2A {     public static void main(String[] args) {         Scanner scanner = new Scanner(System.in);         int t = Integer.parseInt(scanner.nextLine());         int n;        int idx;        int[] grannyReq = new int[100001];         for (int i = 0; i < t; i++) {             n = Integer.parseInt(scanner.nextLine());             for (int j = 0; j < n; j++) {                grannyReq[j] = scanner.nextInt();            }             scanner.nextLine();             Arrays.sort(grannyReq, 0, n);             idx = 0;             for (int j = 0; j < n; j++) {                if (2 + j > grannyReq[j]) {                    idx = j + 1;                }            }             System.out.println(1 + idx);        }         scanner.close();    }}
