import java.util.*;public class Main{    public static void main(String args[])    {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while(t>0)        {            int a,b;            a = sc.nextInt();            b = sc.nextInt();            int min = Math.min(a,b);            int max = Math.max(a,b);            int area=0;            if(max<=(min*2))            {                area = (int)Math.pow(min*2,2);            }            else            {                area = (int)Math.pow(max,2);            }            System.out.println(area);            t--;        }    }}
