import java.util.*;public class Main{    public static void main(String args[])    {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while(t-- > 0)        {            int n = sc.nextInt();            int ar[] = new int[n];            for(int i=0;i<n;i++)            {                ar[i] = sc.nextInt();            }            Arrays.sort(ar);            int dif = Integer.MAX_VALUE;            for(int i=1;i<n;i++)            {                if(dif>(ar[i]-ar[i-1]))                dif = ar[i]-ar[i-1];            }            System.out.println(dif);        }    }}
