import java.util.*;public class Main{    public static Scanner ayah=new Scanner(System.in);	public static void main(String[] args) 	{		int t,n;		t=ayah.nextInt();		while(t>0)		{		    n=ayah.nextInt();		    int [] arr=new int[n];		    for(int i=0;i<n;i++)		    arr[i]=ayah.nextInt();		    int min=Math.abs(arr[0]-arr[1]);		    for(int i=0;i<n;i++)		    for(int j=0;j<n;j++)		    {if(j==i)		    continue;		    else		    {		        if(Math.abs(arr[i]-arr[j])<=min)		        min=Math.abs(arr[i]-arr[j]);		    }		    }		    System.out.println(min);		    t--;		}			}}
