import java.io.*;import java.util.Arrays;import java.util.StringTokenizer;// نورت الكود يا كبير اتفضل // يا رب Acceptedpublic class problem_1 {    public static void main(String[] args) {        FastReader in = new FastReader();        PrintWriter out = new PrintWriter(new BufferedOutputStream(System.out));         int t = in.nextInt();        while (t-- > 0) {            int n = in.nextInt();            Integer[] arr = new Integer[n];            for (int i = 0; i < n; i++)                arr[i] = in.nextInt();            Arrays.sort(arr);            int diff = Integer.MAX_VALUE;            for (int i = 0; i < n - 1; i++) {                int difft = arr[i + 1] - arr[i];                diff = Math.min(diff, difft);            }             out.println(diff);         }         out.close();    }     private static class FastReader {        BufferedReader br;        StringTokenizer st;         FastReader() {            br = new BufferedReader(new                    InputStreamReader(System.in));        }         String next() {            while (st == null || !st.hasMoreElements()) {                try {                    st = new StringTokenizer(br.readLine());                } catch (IOException e) {                    e.printStackTrace();                }            }            return st.nextToken();        }         int nextInt() {            return Integer.parseInt(next());        }         long nextLong() {            return Long.parseLong(next());        }         double nextDouble() {            return Double.parseDouble(next());        }         String nextLine() {            String str = "";            try {                str = br.readLine();            } catch (IOException e) {                e.printStackTrace();            }            return str;        }    }}	    			 			 	   	  		 		   	 	
