//package Coding; import java.io.*;import java.util.*; public class CodeForces {    public static void main(String[] args) throws IOException {        Scanner in = new Scanner(System.in);        // Even + odd = odd        // Sum of n odd number: n odd: odd; n even: even        int n = in.nextInt();        for (int i = 0; i < n; ++i) {            int m = in.nextInt();            int c = in.nextInt();            int numOdd = 0;            int numEven;            int maxOdd;            for (int j = 0; j < m; j++) {                if (in.nextInt() % 2 != 0) {                    numOdd++;                }            }            maxOdd = Math.min(numOdd, c);            numEven = m - numOdd;            if (maxOdd % 2 != 0) {                System.out.println("YES");            } else {                if (maxOdd == 0) {                    System.out.println("NO");                } else {                    int evenNeed = c - (maxOdd - 1);                    if (numEven >= evenNeed) {                        System.out.println("YES");                    } else {                        System.out.println("NO");                    }                }            }            }    }  }          
