import java.util.*;import java.io.*; public class Main2 {	static class FastReader{         BufferedReader br;         StringTokenizer st;         public FastReader(){br = new BufferedReader(new InputStreamReader(System.in));}        String next(){while (st == null || !st.hasMoreElements()){try{st = new StringTokenizer(br.readLine());}         catch (IOException  e){e.printStackTrace();}}return st.nextToken();}        int nextInt(){ return Integer.parseInt(next());}long nextLong(){return Long.parseLong(next());}double nextDouble(){return Double.parseDouble(next());}        String nextLine(){String str = ""; try{str = br.readLine(); } catch (IOException e) {e.printStackTrace();} return str; }     }	static int mod = (int)(1e9+7);	static FastReader sc = new FastReader();	static PrintWriter out = new PrintWriter(System.out);	public static void main (String[] args) {		int z = 1;    	z = sc.nextInt();    	while(z-->0) {    		int n = sc.nextInt();    		int a[] = new int[n];    		for(int i=0;i<n;i++) a[i] = sc.nextInt();    		List<Integer> ans = new LinkedList<>();    		ans.add(a[0]);    		for(int i=1;i<n-1;i++) {    			if(a[i]>a[i-1] && a[i]>a[i+1]) {    				ans.add(a[i]);    			}    			else if(a[i]<a[i-1] && a[i]<a[i+1]) {    				ans.add(a[i]);    			}    		}    		ans.add(a[n-1]);    		out.write((ans.size())+"\n");    		for(int val : ans)    			out.write(val+" ");    		out.write("\n");		}		out.close();	}}
