// First Tryimport java.io.*; import java.util.*;  public  class Substring  {      public static void main(String[] args) throws IOException     {       BufferedReader br = new BufferedReader(new InputStreamReader(System.in));       int t = Integer.parseInt(br.readLine());        while (t-- > 0)        {                String line = br.readLine();            int n=line.length();            String xyz=""+ line.charAt(0);            for (int i = 1; i < n-1; i+=2)             xyz+= line.charAt(i);            xyz+=line.charAt(n-1);            System.out.println(xyz);        }    }}
