import java.util.*;public class Main{    public static void main(String args[])    {        Scanner sc=new Scanner(System.in);        int n=sc.nextInt();        for(int j=0;j<n;j++)        {            String s=sc.next();            int l=s.length();            System.out.print(s.charAt(0));            for(int i=2;i<l;i=i+2)            {                System.out.print(s.charAt(i));            }            System.out.print(s.charAt(l-1));            System.out.println();        }    }}
