// Dummy textimport java.io.*; import java.util.*;  public  class good_Array  {      public static void main(String[] args) throws IOException     {       BufferedReader br = new BufferedReader(new InputStreamReader(System.in));       int t = Integer.parseInt(br.readLine());        while (t-- > 0)        {            int n = Integer.parseInt(br.readLine());           int arr[] = new int[n];           // to read multiple integers line             String line = br.readLine();             String[] strs = line.trim().split("\\s+");             // array elements input             for (int i = 0; i < n; i++)             arr[i] = Integer.parseInt(strs[i]);            int counte=0;            int counto=0;            for(int j=0;j<n;j++)            {                if(arr[j]%2!=j%2)                {                    if(j%2==0)                    counte++;                    else                     counto++;                }            }            if(counte==counto)            System.out.println(counte);            else            System.out.println("-1");        }        }    }                                                                                                                                    
