def answer(n, a):    a.insert(0, 0) #1-based    e = [0] #1-based    o = [0] #1-based    for i in range(1, (2*n)+ 1, 1):        if a[i] % 2 == 0:            e.append(i)        else:            o.append(i)    # list of evens in e and list of odds in o.    fe = ((len(e)-1) // 2) * 2    fo = ((len(o)-1) // 2) * 2    # possible to print too many, so use counter.    ctr = 1    for i in range(1, fe+1, 2):        if ctr <= n-1:            print(e[i], e[i+1])            ctr += 1    for i in range(1, fo+1, 2):        if ctr <= n-1:            print(o[i], o[i+1])            ctr += 1    returnÂ def main():    t = int(input())    n = [0] * t    a = [0] * t    for j in range(t):        n[j] = int(input())        a[j] = [int(i) for i in input().split()]        for j in range(t):        answer(n[j], a[j])Â main()
