# https://codeforces.com/problemset/problem/138/Aimport sys  VOWELS = {"a", "e", "i", "o", "u"}  def parse_quatrain(lines, k: int) -> str:    q = dict()     out = {        "aaaa": False,        "aabb": False,        "abab": False,        "abba": False,    }     for i, l in enumerate(lines):        rhyme = extract_rhyme(l.strip(), k)        if not rhyme:            return out        q[i] = rhyme     if q[0] == q[1] and q[2] == q[3]:        out["aabb"] = True     if q[0] == q[2] and q[1] == q[3]:        out["abab"] = True     if q[0] == q[3] and q[1] == q[2]:        out["abba"] = True     if all(v == q[0] for r,v in q.items()):        out["aaaa"] = True     return out  def extract_rhyme(line: str, k: int):    out = ""    count = 0    for c in reversed(line):        out = c + out         if c in VOWELS:            count += 1            if count == k:                return out     return None  i, k = map(int, sys.stdin.readline().split()) outs = []for _ in range(i):    lines = []    for j in range(4):        lines.append(sys.stdin.readline())    outs.append(parse_quatrain(lines, k)) choices = ["aaaa", "aabb", "abab", "abba"]for c in choices:    if all(o[c] for o in outs):        print(c)        breakelse:    print("NO")
