//http://codeforces.com/problemset/problem/144/D #pragma comment(linker, "/STACK:102400000,102400000")#include <stdio.h>#include <stdlib.h>#include <string.h>#include <limits.h>#include <malloc.h>#include <ctype.h>#include <math.h>#include <string>#include <iostream>#include <algorithm>using namespace std;#include <stack>#include <queue>#include <vector>#include <deque>#include <set>#include <map> using namespace std; int has[20];int has2[20];int has3[20];char str[100010]; char str1[100010];char str2[100010]; int main(){	scanf("%s",str);	memset(has,0,sizeof has); 	for(int i=0;str[i];i++)	{		str1[i]=str2[i]=str[i];		has[str[i]-'0']++;	}	int len=strlen(str);	str1[len]=str2[len]=0;	//cout<<a<<endl;	for(int i=0;i<20;i++)	{		has2[i]=has[i];		has3[i]=has[i];	}	int ans=0;	int num=0;	int flag=-1;	for(int i=1;i<=9;i++)	{//找另一个加和为 10的  当最后一个		num=0; 		if(has[i]&&has2[10-i])		{			has[i]--;			has2[10-i]--;			num++;			int tem; 			for(int j=0;j<=9;j++)			{				tem=min(has[j],has2[9-j]);				num+=tem;				has[j]-=tem;				has2[9-j]-=tem; 			}  			tem=min(has[0],has2[0]);						num+=tem;			has[0]-=tem;			has2[0]-=tem;		}		//ans=max(ans,num);		if(ans<num)		{			ans=num;			flag=i;		}				for(int j=0;j<20;j++)		{			has2[j]=has3[j];			has[j]=has3[j];		}	} 	ans=max(ans,has[0]);	int t1,t2;	t1=t2=len-1;	str1[len]=str2[len]=0;	if(flag!=-1)//凑 10  然后 凑 0 然后 凑其他	{		str1[t1--]=flag+'0';		str2[t2--]=10-flag+'0';		has[flag]--;		has2[10-flag]--; 		for(int i=0;i<=9;i++)		{			int tem=min(has[i],has2[9-i]); 			has[i]-=tem;			has2[9-i]-=tem; 			int tt=tem;			while(tt--)			{				str1[t1--]=i+'0';				str2[t2--]=9-i+'0';			}		} 	} 	int ling=0;	ling=min(has[0],has2[0]);	has[0]-=ling;	has2[0]-=ling; 	for(int i=0;i<=9;i++)	{		while(has[i]--)			str1[t1--]=i+'0';		while(has2[i]--)			str2[t2--]=i+'0';	} 	//printf("%s\n%s\n",str1+ling,str2+ling);	printf("%s",str1+ling);	int tt=ling;	while(tt--)		printf("0");	puts(""); 	printf("%s",str2+ling);	while(ling--)		printf("0");	puts("");		return 0;}
