//KEKWimport java.util.*;import java.io.*;import java.math.*;Â    public class B   {      public static void main(String hi[]) throws Exception      {         BufferedReader infile = new BufferedReader(new InputStreamReader(System.in));         StringTokenizer st = new StringTokenizer(infile.readLine());         int T = Integer.parseInt(st.nextToken());         StringBuilder sb = new StringBuilder();         while(T-->0)         {            int N = Integer.parseInt(infile.readLine());            int[] arr = new int[N];            st = new StringTokenizer(infile.readLine());            for(int i=0; i < N; i++)               arr[i] = Integer.parseInt(st.nextToken());            int dex = -1;            for(int i=0; i < N; i++)               if(arr[i] != 1)               {                  dex = i;                  break;               }            if(dex == -1)            {               if(N%2 == 1)                  sb.append("First\n");               else                  sb.append("Second\n");            }            else            {               if(dex%2 == 0)                  sb.append("First\n");               else                  sb.append("Second\n");            }         }         System.out.print(sb);      }   }
