import java.util.*;import java.math.*;public class CommonPrefixes{	public static void main(String[] args)		{			Scanner sc = new Scanner(System.in);			int runs = sc.nextInt();			boolean a = true;			while(runs-->0)			{				int size = sc.nextInt();				int num = 0;				char[] s = new char[200];				Arrays.fill(s,'a');				System.out.println(String.valueOf(s));				for(int i = 0;i<size;i++)				{					int temp = sc.nextInt();					if(s[temp]=='a')						s[temp]='b';					else						s[temp]='a';					System.out.println(String.valueOf(s));				}			}		}}
