// 139A - Petr and Book// https://codeforces.com/problemset/problem/139/A import java.util.Scanner; public class CF139A {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int[] week = new int[7];        for (int i = 0; i < 7; i++) {            week[i] = sc.nextInt();        }        int count = 0;        while (n > 0) {            for (int i = 0; i < 7; i++) {                if (n - week[i] > 0) {                    n -= week[i];                    count++;                    continue;                }                n = -1;                break;             }        }        System.out.println((count % 7) + 1);        sc.close();     }}
