# link: https://codeforces.com/problemset/problem/14/Bif __name__ == "__main__":    n,x = map(int,input().split())    a = [0 for i in range(1006)]    temp = 0    answer = float('inf')    for i in range(n):        info = list(map(int, input().split()))        info.sort()        a[info[0]] += 1        a[info[1]+1] -= 1    for i in range(1006):        temp += a[i]        if temp == n:            answer = min(answer, abs(x-i))    if answer == float('inf'):        print(-1)    else:        print(answer)                                         
