#!/usr/bin/env python3 import sysimport math EPS = 1e-9def solve(n, R, r):   if r > R:      return False   if n == 1:      return True   if n == 2:      return 4*r <= 2*R      side_len = 2*(R-r)*math.sin(math.pi / n)#  print(side_len, file=sys.stderr)    return 2*r <= side_len + EPS input = iter(sys.stdin.read().splitlines()).__next__ n, R, r = map(int, input().split()) res = solve(n, R, r)print('YES' if res else 'NO') 
