import java.util.*;public class Practice{     void solve()     {          Scanner sc=new Scanner(System.in);          int t=sc.nextInt();          while(t-->0)          {               int n=sc.nextInt();               String bin=sc.next();               String ans="";               for(int i=0;i<bin.length();i+=2)                    ans+=""+bin.charAt(i);               System.out.println(ans);          }     }     public static void main(String[] args) {          Practice p=new Practice();          p.solve();     }}
