# Problem: B. Arrays Sum# Contest: Codeforces - Grakn Forces 2020# URL: https://codeforces.com/problemset/problem/1408/B# Memory Limit: 256 MB# Time Limit: 1000 ms# # KAPOOR'S from sys import stdin, stdout  def INI():	return int(stdin.readline())	def INL():	return [int(_) for _ in stdin.readline().split()]	def INS():	return stdin.readline() def MOD():    return pow(10,9)+7	def OPS(ans):	stdout.write(str(ans)+"\n")	def OPL(ans):	[stdout.write(str(_)+" ") for _ in ans]	stdout.write("\n") 	if __name__=="__main__":	for _ in range(INI()):		n,k=INL()		x=len(set(INL()))		ans=0		i=0		if k==1 and k<x:			OPS(-1)		else:			while 0<x:				if i==0:					ans+=1					x-=k				else:					ans+=1					x=x-(k-1)				i+=1			OPS(ans)
