    //package Random;     import java.io.BufferedReader;    import java.io.IOException;    import java.io.InputStreamReader;    import java.lang.reflect.Array;    import java.util.*;     public class Main {         static class FastReader {            BufferedReader br;            StringTokenizer st;             public FastReader() {                br = new BufferedReader(new                        InputStreamReader(System.in));            }             String next() {                while (st == null || !st.hasMoreElements()) {                    try {                        st = new StringTokenizer(br.readLine());                    } catch (IOException e) {                        e.printStackTrace();                    }                }                return st.nextToken();            }             int nextInt() {                return Integer.parseInt(next());            }             long nextLong() {                return Long.parseLong(next());            }             double nextDouble() {                return Double.parseDouble(next());            }             String nextLine() {                String str = "";                try {                    str = br.readLine();                } catch (IOException e) {                    e.printStackTrace();                }                return str;            }        }         public static void main(String[] args) {            FastReader sc = new FastReader();            int t=sc.nextInt();               while(t-->0) {                 int n = sc.nextInt();                int k = sc.nextInt();                 int[]arr = new int[n];                Set<Integer> s = new HashSet<>();                for(int i=0;i<n;i++) {                    arr[i] = sc.nextInt();                    s.add(arr[i]);                }                 int diff=s.size();                if(s.size()==1 && k>1){                    System.out.println(1);                }                else if(k==1 && s.size()>1){                    System.out.println(-1);                }                else if(k>=s.size()){                    System.out.println(1);                }                else{                    int ans= 1;                    diff-=k;                    //System.out.println(diff);                    if(diff>0) {                        ans += (int) Math.ceil(diff / ((k - 1) * 1.0));                    }                    System.out.println(ans);                 }             }        }     } 
