import java.util.*;public class Solutions {	public static void main(String[] args) {		Scanner s=new Scanner(System.in);		int t=s.nextInt();		while(t-->0) {			int a=s.nextInt();			int b=s.nextInt();			int diff=Math.abs(a-b);			int sum=10;int step=0;			while(diff!=0) {				if(diff>=sum) {					int p=diff/sum;					diff=diff-p*sum;					step+=p;				}else {					sum--;				}			}			System.out.println(step);		}}}
