# include<bits/stdc++.h>using namespace std;Â void LINE(vector<int> &vec){  int N = vec.size();  for(int i=0; i<N; i++){    if(i == N-1){      cout << vec[i] << endl;    }    else{      cout << vec[i] << " ";    }  }}  void solve(){  int N;  cin >> N;  vector<int> vec(N);  for(int i=0; i<N; i++){    cin >> vec[i];  }  vector<int> B(N);  for(int i=0; i<N; i++){    if(i %2 == 0){      B[i] = vec[i+1];      if(vec[i]*B[i] > 0){        B[i] *= -1;      }    }    else{      B[i] = vec[i-1];      if(vec[i]*B[i] < 0){        B[i] *= -1;      }    }  }  LINE(B);}int main(){  ios::sync_with_stdio(false);  cin.tie(nullptr);  int T;  cin >> T;  //T = 1;  for(int i=0; i<T; i++){    solve();  }}
