import java.util.*;public class Main {    public static void main(String[]args) {        Scanner sc = new Scanner(System.in);        int t = sc.nextInt();        while(t-- > 0) {            int n = sc.nextInt();            int m = sc.nextInt();            int r = sc.nextInt();            int c = sc.nextInt();            int max = Math.abs(1-r) + Math.abs(1-c);            max = Math.max(max, (Math.abs(n-r) + Math.abs(1-c)));            max = Math.max(max, (Math.abs(n-r) + Math.abs(m-c)));            max = Math.max(max, (Math.abs(1-r) + Math.abs(m-c)));            System.out.println(max);        }    }}
