//      これを翻訳している間、あなたはあなたの人生のいくつかの貴重な瞬間を無駄にしました import java.io.*;import java.math.BigDecimal;import java.math.BigInteger;import java.util.*;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;  public class Main1 {       public static void main(String[] args) {           FastScanner in = new FastScanner();        PrintWriter out = new PrintWriter(System.out) ;          int t = in.nextInt() ;        while (t-- > 0){             int n = in.nextInt() ;            int k = in.nextInt();            int max = -1 ;            int A[] = new int[n] ;             for (int i = 0; i <n ; i++) {                A[i] = in.nextInt();                max = Math.max(max , A[i]) ;            }            int min = Integer.MAX_VALUE ;             for (int i = 1; i <= max ; i++) {                int ct =  0 ;                 for (int j = 0; j <n ; j++ ) {                    if (A[j] == i)continue;                    ct++ ;                    j += k-1 ;                 }                min = Math.min(min , ct) ;             }             System.out.println(min);         }                out.flush();out.close();    }      static class FastScanner {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        StringTokenizer st = new StringTokenizer("");         String next() {            while (!st.hasMoreTokens())                try {                    st = new StringTokenizer(br.readLine());                } catch (IOException e) {                    e.printStackTrace();                }            return st.nextToken();        }         int nextInt() {            return Integer.parseInt(next());        }         int[] readArray(int n) {            int[] a = new int[n];            for (int i = 0; i < n; i++) a[i] = nextInt();            return a;        }          long nextLong() {            return Long.parseLong(next());        }    }  }                                
