def main():        t = int(input())


for _ in range(t):        n = int(input())
a = [
        0] + readIntArr()  # condition 1: total must be divisible by n        total=sum(a)        if total%n!=0:            print(-1)            continue        avg=total//n        ans=[]        #strategy: all a[i]->a[1], then a[1]->a[i]        for i in range(2,n+1):            target=i*((a[i]+i-1)//i) #next largest multiple of i            diff=target-a[i]            if diff>0:                a[i]+=diff #a[i] now a multiple of i                a[1]-=diff                ans.append([1,i,diff])                        x=a[i]//i            if x>0:                a[i]-=x*i #a[i] now 0                a[1]+=x*i                ans.append([i,1,x])        #now a[i]==0 for all i>1        for i in range(2,n+1):            ans.append([1,i,avg]) #transfer avg to a[i] for i>1                print(len(ans))        multiLineArrayOfArraysPrint(ans)#        print(a)                return import sysinput=sys.stdin.buffer.readline #FOR READING PURE INTEGER INPUTS (space separation ok)#import sys#input=lambda: sys.stdin.readline().rstrip("\r\n") #FOR READING STRING/TEXT INPUTS. def oneLineArrayPrint(arr):    print(' '.join([str(x) for x in arr]))def multiLineArrayPrint(arr):    print('\n'.join([str(x) for x in arr]))def multiLineArrayOfArraysPrint(arr):    print('\n'.join([' '.join([str(x) for x in y]) for y in arr])) def readIntArr():    return [int(x) for x in input().split()] main()
