//package codechef;Â import java.io.*;import java.util.*;public class CodeChef{    public static void main(String[] args)throws IOException    {        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        int t = Integer.parseInt(br.readLine());        while(t-->0)        {            String []str = br.readLine().split(" ");            int n = Integer.parseInt(str[0]), k = Integer.parseInt(str[1]);            String s[] = br.readLine().split(" ");            int arr[] = new int[n];            for(int i = 0; i<n; i++)            {                arr[i] = Integer.parseInt(s[i]);            }            Arrays.sort(arr);            int count = 0, x = arr[0];            for(int i = 1; i<n; i++)            {                if(arr[i]+x>k) break;                while(arr[i]+x<=k)                {                    count++;                    arr[i] = arr[i]+x;                }            }            while(arr[0]+arr[1]<=k)            {                count++;                arr[0] = arr[0] + arr[1];            }            System.out.println(count);        }    }}
